/* styles.css */

body {
    margin: 0;
    padding: 0;
    background-color: black;
    color: white;
    font-family: Arial, sans-serif;
}

#intro {
    height: 100vh;
    display: flex;
    flex-direction: column;
    justify-content: center;
    align-items: center;
}

#brand-name {
    font-size: 5rem;
    animation: fadeIn 2s forwards;
}

@keyframes fadeIn {
    0% {
        opacity: 0;
        transform: scale(0.8);
    }
    100% {
        opacity: 1;
        transform: scale(1);
    }
}

#explore-button {
    font-size: 2rem;
    padding: 1rem 2rem;
    border: none;
    border-radius: 10px;
    background-color: #4CAF50;
    color: white;
    cursor: pointer;
    animation: bounce 2s infinite;
    display: none; /* Initially hidden */
}

@keyframes bounce {
    0%, 100% {
        transform: translateY(0);
    }
    50% {
        transform: translateY(-10px);
    }
}

#features {
    height: 100vh;
    display: flex;
    flex-direction: column;
    justify-content: center;
    align-items: center;
}

#features-container {
    display: flex;
    justify-content: center;
    align-items: center;
}

.feature-box {
    width: 30%;
    height: 30vh;
    background-color: #333;
    display: flex;
    justify-content: center;
    align-items: center;
    font-size: 2rem;
    cursor: pointer;
    transition: transform 0.5s, font-size 0.5s;
    margin: 0 20px; /* Add spacing between feature boxes */
}

.feature-box:hover {
    transform: scale(1.2);
    font-size: 2.4rem; /* Increase font-size on hover */
}

.feature-title {
    margin: 0;
}

#design-images, #sound-videos {
    display: flex;
    justify-content: space-between;
    width: 80%;
}

#design-images img, #sound-videos video {
    width: 30%;
    height: 30vh;
    object-fit: cover;
    cursor: pointer;
    transition: transform 0.5s;
    margin: 0 10px; /* Added spacing */
}

#design-images img:hover, #sound-videos video:hover {
    transform: scale(1.1);
}

#models-table {
    border-collapse: collapse;
    width: 80%;
}

#models-table th, #models-table td {
    border: 1px solid white;
    padding: 1rem;
    text-align: center;
}

#back-to-features, #back-to-home {
    font-size: 2rem;
    padding: 1rem 2rem;
    border: none;
    border-radius: 10px;
    background-color: #4CAF50;
    color: white;
    cursor: pointer;
    display: block;
    margin-top: 20px;
}

.content {
    opacity: 0;
    transform: translateY(20px);
    transition: opacity 2s, transform 2s;
}

.animate-content {
    opacity: 1;
    transform: translateY(0);
}

.button {
    opacity: 0;
    transform: translateY(20px);
    transition: opacity 2s, transform 2s;
}

.animate-button {
    opacity: 1;
    transform: translateY(0);
}

/* Media Queries */
@media only screen and (max-width: 768px) {
    #features-container {
        flex-direction: column;
    }
    .feature-box {
        width: 100%;
        height: 30vh;
        margin-bottom: 2rem;
    }
    #design-images, #sound-videos {
        flex-direction: column;
    }
    #design-images img, #sound-videos video {
        width: 100%;
        height: 30vh;
        margin-bottom: 2rem;
    }
}

/* Animation for section titles */
.section-title {
    opacity: 0;
    transform: translateY(-20px);
    transition: opacity 2s, transform 2s;
}

.section-title.animate-title {
    opacity: 1;
    transform: translateY(0);
}

document.addEventListener("DOMContentLoaded", () => {
    // Elements
    const brandName = document.getElementById("brand-name");
    const exploreButton = document.getElementById("explore-button");
    const featuresSection = document.getElementById("features");
    const designBox = document.getElementById("design-box");
    const soundBox = document.getElementById("sound-box");
    const modelsBox = document.getElementById("models-box");
    const designSection = document.getElementById("design-section");
    const soundSection = document.getElementById("sound-section");
    const modelsSection = document.getElementById("models-section");
    const backToFeaturesButtons = document.querySelectorAll("#back-to-features");
    const backToHomeButton = document.getElementById("back-to-home");

    // Show Explore button after title animation
    brandName.addEventListener("animationend", () => {
        exploreButton.style.display = "block";
    });

    // Function to show section with animation
    const showSection = (section) => {
        document.querySelectorAll('section').forEach(sec => sec.style.display = 'none'); // Hide all sections
        section.style.display = 'block'; // Show the target section
        const title = section.querySelector('.section-title');
        const content = section.querySelectorAll('.content');
        const buttons = section.querySelectorAll('button');
        if (title) {
            title.classList.remove('animate-title'); // Reset animation
            void title.offsetWidth; // Trigger reflow to restart animation
            title.classList.add('animate-title'); // Animate title
            title.addEventListener('animationend', () => {
                content.forEach(item => {
                    item.classList.add('animate-content');
                });
                buttons.forEach(button => {
                    button.classList.add('animate-button');
                });
            });
        }
    };

    // Click event to navigate to features section
    exploreButton.addEventListener("click", () => {
        showSection(featuresSection);
    });

    // Click events for individual feature boxes to navigate to respective sections
    designBox.addEventListener("click", () => showSection(designSection));
    soundBox.addEventListener("click", () => showSection(soundSection));
    modelsBox.addEventListener("click", () => showSection(modelsSection));

    // Back to features
    backToFeaturesButtons.forEach(button => {
        button.addEventListener("click", () => {
            showSection(featuresSection);
        });
    });

    // Back to home
    backToHomeButton.addEventListener("click", () => {
        showSection(document.getElementById("intro"));
    });

    // Play videos on hover
    document.querySelectorAll('video').forEach(video => {
        video.addEventListener('mouseover', () => {
            video.play();
        });
        video.addEventListener('mouseout', () => {
            video.pause();
        });
    });
});
